INSERT INTO products (product_name, categoryid, supplierid, brandid, price, description, imageurl, stock_quantity) VALUES
    ('Bvlgari Aqva Pour Homme', 1, 1, 1, 2450000, N'Mang đến cảm giác như đắm mình trong làn nước biển trong lành, Aqva Pour Homme mở đầu với hương cam bergamot và quả cam quýt Sicily. Lớp hương giữa đưa bạn vào thế giới biển cả với rong biển Posidonia và hoa lavender, kết thúc bằng hương gỗ hổ phách và xạ hương ấm áp, gợi lên hình ảnh một người đàn ông mạnh mẽ nhưng tràn đầy sức sống.', 'https://product.hstatic.net/1000340570/product/bvlgari-aqva-pour-homme-100ml_a809dabd88c246088c256ff0f18658cb_master.jpg', 15),

    ('Yves Saint Laurent Mon Paris', 2, 2, 2, 2950000, N'Mon Paris là sự kết hợp tinh tế giữa các hương hoa và trái cây, mang đến cảm giác ngọt ngào và say đắm. Hương đầu mở ra với sự tươi mát của quả dâu tây, quả mâm xôi, quả lê và cam bergamot. Tiếp đến là hương giữa với sự hòa quyện của hoa nhài Sambac, hoa cam, hoa cà độc dược và hoa mẫu đơn trắng. Cuối cùng, hương cuối để lại dấu ấn ấm áp của hoắc hương Indonesia, xạ hương trắng và gỗ tuyết tùng. Mon Paris được thiết kế để phản ánh niềm đam mê mãnh liệt và tình yêu nồng cháy, giống như một cuộc hành trình khó quên ở Paris. Thiết kế chai nước hoa với màu hồng nhạt và chiếc nơ đen tinh tế cũng góp phần tôn lên vẻ sang trọng và thanh lịch của sản phẩm.', 'https://product.hstatic.net/1000340570/product/yves-saint-laurent-mon-paris-90ml-edp_b942bd34a30e4d029b6218e811cc57ed_master.jpg', 30),

    ('Chanel Chance Eau Tendre', 2, 3, 3, 3800000, N'Chance Eau Tendre là một hương thơm nữ tính, tươi mát và đầy tinh tế, ra mắt vào năm 2010. Hương đầu mở ra với sự tươi mát của quả bưởi và mộc qua, mang lại cảm giác sảng khoái và tràn đầy năng lượng. Tiếp đến là hương giữa với sự hòa quyện của hoa nhài và lan dạ hương, tạo nên một tầng hương đầy nữ tính và quyến rũ. Cuối cùng, hương cuối để lại dấu ấn êm dịu và đẫy đà của xạ hương, hoa diên vĩ và gỗ tuyết tùng Virginia, mang lại cảm giác thanh lịch và tinh tế. Chance Eau Tendre là sự kết hợp hoàn hảo giữa các thành phần hương liệu, tạo nên một mùi hương vừa nhẹ nhàng, vừa quyến rũ, lý tưởng cho những ngày hè vô tư hoặc các dịp đặc biệt.', 'https://product.hstatic.net/1000340570/product/chanel-chance-eau-tendre-eau-de-toilette-50ml_e108c312d4854ff88fe537f4360993c3_master.jpg', 50),

    ('Dior Miss Dior Blooming Bouquet', 2, 4, 4, 2500000, N'Dior Miss Dior Blooming Bouquet là một hương thơm tươi mát và dịu dàng, như một bó hoa mới nở. Hương thơm mở đầu với sự tươi mát của cam bergamot, mang lại cảm giác sảng khoái và đầy năng lượng. Tiếp đến là sự kết hợp tinh tế của hoa hồng Damascus và hoa mẫu đơn, tạo nên một lớp hương giữa ngọt ngào và quyến rũ. Cuối cùng, xạ hương trắng mang lại sự mềm mại và ấm áp, để lại một dấu ấn nhẹ nhàng và thanh lịch trên da. Đây là một mùi hương thanh lịch và nữ tính, hoàn hảo cho những ai yêu thích sự tươi mới và quyến rũ.', 'https://product.hstatic.net/1000340570/product/dior-miss-dior-blooming-bouquet_5859c56197694d518431954615a3d6f4_master.jpg', 40),

    ('Kilian Black Phantom', 1, 5, 5, 7350000, N'Kilian Black Phantom - "Memento Mori" là một hương nước hoa bí ẩn và quyến rũ. Mở đầu với rượu rum Caribbean và cà phê đậm đà, hương thơm này mang đến cảm giác nguy hiểm và lôi cuốn. Tầng hương giữa là cacao và whisky ngọt ngào, kết thúc với gỗ đàn hương và vetiver sâu lắng. Đây là lựa chọn hoàn hảo cho những ai yêu thích sự bí ẩn và muốn để lại ấn tượng khó quên.', 'https://product.hstatic.net/1000340570/product/kilian-black-phantom-memento-mori_45baadc56dd147599df3860c9b80e852_master.jpg', 13),

    ('Versace Dylan Blue', 1, 6, 6, 2420000, N'Versace Dylan Blue là một hương nước hoa nam tính, mạnh mẽ với sự kết hợp của hương cam bergamot, bưởi và lá vả, mang đến cảm giác tươi mát và sắc nét của vùng Địa Trung Hải. Hương giữa là sự pha trộn của lá violet và hương gỗ, tạo nên sự ấm áp và nam tính. Cuối cùng, hương hoắc hương và xạ hương mang đến sự bền lâu và quyến rũ. Chai nước hoa được thiết kế với màu xanh dương đậm và các chi tiết vàng, gợi nhớ đến biển Địa Trung Hải và văn hóa Hy Lạp - La Mã.', 'https://product.hstatic.net/1000340570/product/dylan-blue_919771324a4b46998825d6dfbdc5764f_master.jpg', 50),

    ('Jean Paul Gaultier Classique Pin-up', 2, 7, 7, 2600000, N'Jean Paul Gaultier Classique Pin-Up là một hương thơm đầy quyến rũ và nữ tính, ra mắt vào năm 2020. Hương thơm mở đầu với sự tươi mát, sảng khoái của gừng và chanh, mang lại cảm giác năng động và tràn đầy sức sống. Tiếp đến là sự ngọt ngào, quyến rũ của hoa cam và kẹo dẻo, tạo nên một lớp hương giữa đầy mê hoặc. Cuối cùng, hương vanilla và hổ phách kết hợp hoàn hảo, để lại một dấu ấn ấm áp và gợi cảm trên da. Classique Pin-Up không chỉ là một mùi hương, mà còn là một tuyên ngôn về sự tự tin và phong cách.', 'https://product.hstatic.net/1000340570/product/jean_paul_gaultier_classique_pin_up_c02dcc3fa3e14b3e9b381be1a0b53625_master.jpg', 20),

    ('Versace Crystal Noir', 2, 6, 6, 2442000, N'Versace Crystal Noir là một hương nước hoa nữ tính, quyến rũ với sự kết hợp của hương bạch đậu khấu, gừng và tiêu. Hương giữa là sự hòa quyện của hoa nhài, hoa huệ và hoa cam, tạo nên sự ngọt ngào và nữ tính. Cuối cùng, hương gỗ đàn hương và xạ hương mang đến sự ấm áp và bền lâu. Crystal Noir được mô tả là một hương thơm huyền bí và gợi cảm, thích hợp cho những buổi tối lãng mạn hoặc các sự kiện đặc biệt.', 'https://product.hstatic.net/1000340570/product/versace-crystal-noir-edt_2a522575d57449a883defc16e0d116ac_master.jpg', 25),

    ('Chanel Allure Homme Sport Eau Extreme', 1, 3, 3, 3650000, N'Allure Homme Sport mang đến một hương thơm tươi mát và năng động. Hương đầu là sự kết hợp của cam, quýt và aldehyde, mang lại cảm giác sảng khoái. Hương giữa là sự hòa quyện của tiêu đen, hoa cam và dầu neroli. Hương cuối là sự ấm áp của đậu tonka, hổ phách và xạ hương trắng. Đây là một mùi hương lý tưởng cho những người đàn ông yêu thích thể thao và các hoạt động ngoài trời.', 'https://product.hstatic.net/1000340570/product/chanel-allure-homme-sport-eau-de-toilette-100ml_b346b3a4de84438bb866718bb8711d2c_master.jpg', 60),

('Dior Hypnotic Poison Eau de Toilette', 2, 4, 4, 2500000, N'Một hương thơm ngọt ngào và gợi cảm với hương đầu của cam bergamot và mận, hương giữa của hoa nhài và hoa cam, và hương cuối của vani và xạ hương. Hypnotic Poison mang lại cảm giác bí ẩn và quyến rũ, lý tưởng cho buổi tối. Hương thơm này còn có sự kết hợp của các nốt hương hổ phách và vani, tạo nên một mùi hương ấm áp và ngọt ngào.', 'https://down-vn.img.susercontent.com/file/vn-11134207-7r98o-ltdcvvpj6404d1.webp', 40),

('Bvlgari Goldea Eau de Parfum', 2, 1, 1, 3600000, N'Bvlgari Goldea Eau de Parfum là một hương thơm nữ tính đầy quyến rũ, tôn vinh ánh sáng vàng rực rỡ và sự sang trọng vĩnh cửu. Hương đầu mở ra với sự tươi mát của cam Bergamot, cam ngọt và xạ hương vàng lấp lánh. Tầng hương giữa gợi cảm với hoa nhài, ylang-ylang và xạ hương mịn màng, tạo nên sự thanh thoát và quyến rũ. Hương cuối đậm đà với nhựa hổ phách, hoắc hương và xạ hương trắng, mang lại cảm giác ấm áp và lưu luyến lâu dài.
Thiết kế chai lấy cảm hứng từ ánh vàng, kết hợp đường cong mềm mại, thể hiện sự nữ tính và quyền lực, là biểu tượng hoàn hảo cho vẻ đẹp vượt thời gian. Goldea phù hợp cho những dịp đặc biệt, khi bạn muốn tỏa sáng với sự tự tin và phong cách riêng.', 'https://product.hstatic.net/1000340570/product/bvlgari-goldea-eau-de-parfum-90ml_fc6cdf1799544a66a96caf2b9f79a762_master.jpg', 25),

    ('Yves Saint Laurent Kouros', 1, 2, 2, 2990000, N'Kouros là một mùi hương mạnh mẽ và đầy nam tính, thể hiện tinh thần chiến thắng và sự chinh phục. Hương đầu mở ra với sự tươi mát của aldehydes, cam bergamot, ngải thơm và ngò rí. Tiếp đến là hương giữa với sự hòa quyện của hoa cẩm chướng, cỏ hương bài, hoắc hương, quế, hoa nhài, rễ cây diên vĩ và phong lữ. Cuối cùng, hương cuối để lại dấu ấn ấm áp của rêu sồi, hổ phách, nhang, mật ong, da thuộc, cầy hương, xạ hương, đậu tonka và vani. Kouros mang đến một mùi hương fougère da thuộc, với sự kết hợp độc đáo giữa các thành phần tạo nên một dấu ấn khó quên. Hương thơm này vừa sạch sẽ, vừa mạnh mẽ, thể hiện sự nam tính và quyến rũ.', 'https://hdperfume.vn/cdn/shop/products/b.png?format=webp&v=1669368303&width=910', 40),

    ('Tom Ford Noir Extreme', 1, 8, 8, 3800000, N'Tom Ford Noir Extreme là một hương nước hoa nam tính, mạnh mẽ với sự kết hợp của các hương liệu ấm áp và gợi cảm. Hương đầu mở ra với sự pha trộn của bạch đậu khấu, nhục đậu khấu, nghệ tây và cam mandarin, mang đến cảm giác tươi mới và đầy sức sống. Hương giữa là sự hòa quyện của hoa hồng, hoa nhài và hoa cam, tạo nên một tầng hương ngọt ngào và quyến rũ. Cuối cùng, hương gỗ đàn hương, hổ phách và vani mang đến sự ấm áp và bền lâu, để lại một dấu ấn mạnh mẽ và nam tính.', 'https://orchard.vn/wp-content/uploads/2024/07/tom-ford-noir-extreme-edp_3.jpg', 30),

    ('Chanel Coco Eau de Toilette', 2, 3, 3, 3050000, N'Chanel Coco Eau de Toilette là một hương thơm nữ tính, ra mắt vào đầu những năm 1980, mang đến sự kết hợp tuyệt hảo giữa cá tính mạnh mẽ và phong cách baroque của Gabrielle Chanel. Hương đầu mở ra với sự tươi mát của cam quýt và ngò thơm. Tiếp đến là hương giữa với sự hòa quyện của hoa nhài, hoa cam và hoàng lan, tạo nên một tầng hương đầy nữ tính và quyến rũ. Cuối cùng, hương cuối để lại dấu ấn ấm áp của vani, đậu tonka, hoắc hương và hổ phách, mang lại cảm giác sang trọng và bí ẩn12. Đây là một mùi hương lý tưởng cho những người phụ nữ yêu thích sự thanh lịch và quyến rũ.', 'https://product.hstatic.net/1000340570/product/chanel-coco-eau-de-toilette_a621a91cdae54dd4837cec6677a67730_master.jpg', 30),
    
    ('Dior Sauvage Eau de Parfum', 1, 4, 4, 3800000, N'Một hương thơm tươi mát và mạnh mẽ với hương đầu của cam bergamot, hương giữa của tiêu và hương cuối của ambroxan. Đây là một mùi hương nam tính và cuốn hút, lý tưởng cho cả ngày và đêm. Sauvage mang lại cảm giác tự do và hoang dã, như một làn gió mới từ thiên nhiên. Hương thơm này được lấy cảm hứng từ những không gian rộng lớn và bầu trời xanh thẳm.', 'https://product.hstatic.net/1000340570/product/dior-sauvage-eau-de-parfum-100ml_e1c611f46daa451f8a159c1652c8d6c1_master.jpg', 25),

('Chanel Bleu de Chanel Eau de Parfum', 1, 3, 3, 3500000, N'Chanel Bleu de Chanel Eau de Parfum là một hương thơm nam tính, mạnh mẽ và cuốn hút, ra mắt vào năm 2010. Hương đầu tươi mát với cam bergamot, chanh và bạc hà. Hương giữa hòa quyện gừng, nhục đậu khấu và hoa nhài, tạo nên sự nam tính và lôi cuốn. Hương cuối ấm áp với gỗ đàn hương, hoắc hương và nhựa hương, mang lại cảm giác sâu lắng và quyến rũ. Đây là mùi hương lý tưởng cho người đàn ông hiện đại, tự tin và quyết đoán.', 'https://product.hstatic.net/1000340570/product/chanel-bleu-de-chanel-eau-de-parfum-100ml_d94a8cd0feb743b59a4e612a651b7390_master.jpg', 30),

    ('Versace Eros', 1, 6, 6, 2590000, N'Versace Eros là một hương nước hoa nam tính, mạnh mẽ với sự kết hợp của hương bạc hà, táo xanh và chanh Ý. Hương giữa là sự pha trộn của đậu tonka, hoa phong lữ và ambroxan, tạo nên sự ấm áp và gợi cảm. Cuối cùng, hương vani, rêu sồi và gỗ tuyết tùng mang đến sự bền lâu và quyến rũ. Chai nước hoa màu xanh dương đậm với biểu tượng Medusa và họa tiết Hy Lạp cổ điển, thể hiện sự mạnh mẽ và quyền lực của thần Eros.', 'https://product.hstatic.net/1000340570/product/versace-eros-for-men_e21f596ba1f6467eb39ace8813943882_master.jpg', 22),

