CREATE TRIGGER AddInvoiceAfterOrderInsert
ON orders
AFTER INSERT
AS
BEGIN
    -- Thêm dữ liệu vào bảng Invoices cho tất cả các dòng được chèn trong câu lệnh INSERT
    INSERT INTO invoices (orderid, invoice_date, total_amount, payment_method)
    SELECT 
        i.orderid,  -- Lấy OrderID từ bảng Orders
        GETDATE(),  -- Ngày giờ hiện tại khi tạo hóa đơn (thay vì OrderDate, vì InvoiceDate có thể là thời gian tạo hóa đơn)
        i.total_amount - 
            COALESCE(
                LEAST(
                    i.total_amount * d.discount_percentage / 100, 
                    d.max_discount_amount
                ), 
                0
            ) AS finalamout,  -- Tính số tiền sau khi giảm giá
        i.payment_method  -- Lấy PaymentMethod từ bảng Orders
    FROM Inserted i
    LEFT JOIN dbo.discounts d ON i.discountid = d.discountid;  -- Kết nối với bảng Discounts để lấy thông tin giảm giá
END;
GO


CREATE TRIGGER UpdatePaymentStatus
ON orders
AFTER UPDATE
AS
BEGIN
    -- Bắt đầu một transaction
    SET NOCOUNT ON;

    -- Cập nhật trạng thái thanh toán thành "đã thanh toán" nếu trạng thái giao hàng là "đã giao"
    UPDATE orders
    SET payment_status = N'Đã thanh toán'
    FROM orders o
    INNER JOIN Inserted i ON o.orderid = i.orderid
    WHERE i.delivery_status = N'Đã giao';
END;
GO
